mapper.select:
SELECT
    "CNT_TRY_ANS"
FROM
    "TN_TRAIN_EXAMRESULT_TEAM"
WHERE
    "TR_USER_GRP" = #{tr_user_grp}
    AND "TEAM_CD" = #{team_cd}
    AND "TR_NUM" = #{tr_num}
    AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
    AND "TR_EXAM_ID" = #{tr_exam_id}

SELECT
    "STAT_ID"
FROM "TN_TRAIN_EXAMRESULT"
WHERE
    "TR_USER_ID" = #{tr_user_id}
AND    "TR_NUM" = #{tr_num}
AND    "TR_EXAM_GRPID" = #{tr_exam_grpid}
AND    "TR_EXAM_ID" = #{tr_exam_id}

SELECT
    "HINT_USE"
FROM
    "TN_TRAIN_EXAMRESULT_TEAM"
WHERE
"TR_USER_GRP" = #{tr_user_grp}
AND "TEAM_CD" = #{team_cd}
AND "TR_NUM" = #{tr_num}
AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
AND "TR_EXAM_ID" = #{tr_exam_id}

mapper.update:
UPDATE "TN_TRAIN_EXAMRESULT_TEAM"
SET
    "RESULT_SCORE" = "RESULT_SCORE"-#{result_score},
    "HINT_USE" = 1
WHERE
"TR_USER_GRP" = #{tr_user_grp}
AND "TEAM_CD" = #{team_cd}
AND "TR_NUM" = #{tr_num}
AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
AND "TR_EXAM_ID" = #{tr_exam_id}

UPDATE "TN_TRAIN_EXAMRESULT"
SET
    "RESULT_SCORE" = "RESULT_SCORE"-#{result_score},
    "HINT_USE" = 1
WHERE
    "TR_USER_ID" = #{tr_user_id}
AND    "TR_NUM" = #{tr_num}
AND    "TR_EXAM_GRPID" = #{tr_exam_grpid}
AND    "TR_EXAM_ID" = #{tr_exam_id}

UPDATE
    "TN_TRAIN_EXAMRESULT_TEAM"
SET
  "ANSWER_USER_ID" = #{answer_user_id},
  "INPUT_ANSWER" = #{input_answer},
  "CNT_TRY_ANS" = 1
WHERE
    "TR_USER_GRP" = #{tr_user_grp}
    AND "TEAM_CD" = #{team_cd}
    AND "TR_NUM" = #{tr_num}
    AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
    AND "TR_EXAM_ID" = #{tr_exam_id}

UPDATE
"TN_TRAIN_EXAMRESULT_TEAM"
SET
"ANSWER_USER_ID" = #{answer_user_id},
"INPUT_ANSWER" = #{input_answer},
"RESULT_SCORE" = "RESULT_SCORE" + #{point},
"CNT_TRY_ANS" = 2
WHERE
"TR_USER_GRP" = #{tr_user_grp}
AND "TEAM_CD" = #{team_cd}
AND "TR_NUM" = #{tr_num}
AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
AND "TR_EXAM_ID" = #{tr_exam_id}

UPDATE
    "TN_TRAIN_EXAMRESULT_TEAM"
SET
    "ANSWER_USER_ID" = #{answer_user_id},
    "INPUT_ANSWER" = #{input_answer},
    "RESULT_SCORE" = "RESULT_SCORE" - #{result_score} + #{point},
    "CNT_TRY_ANS" = 2
WHERE
    "TR_USER_GRP" = #{tr_user_grp}
    AND "TEAM_CD" = #{team_cd}
    AND "TR_NUM" = #{tr_num}
    AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
    AND "TR_EXAM_ID" = #{tr_exam_id}

UPDATE
    "TN_TRAIN_EXAMRESULT_TEAM"
SET
    "ANSWER_USER_ID" = #{answer_user_id},
    "INPUT_ANSWER" = #{input_answer},
    "RESULT_SCORE" = 0,
    "CNT_TRY_ANS" = 2
WHERE
    "TR_USER_GRP" = #{tr_user_grp}
    AND "TEAM_CD" = #{team_cd}
    AND "TR_NUM" = #{tr_num}
    AND "TR_EXAM_GRPID" = #{tr_exam_grpid}
    AND "TR_EXAM_ID" = #{tr_exam_id}
